$(document).ready(function(){$(".js-cancel-shift").click(function(n){var t=$(n.target);bootbox.dialog({message:"Are you sure you want to cancel this shift?",title:"Confirm",buttons:{no:{label:"No",className:"btn-default",callback:function(){bootbox.hideAll()}},yes:{label:"Yes",className:"btn-danger",callback:function(){$.ajax({url:"/api/projects/"+t.attr("data-project-id")+"/shifts/delete/"+t.attr("data-shift-id"),method:"DELETE"}).done(function(){t.parents("tr").fadeOut(function(){$(this).remove()})}).fail(function(){bootbox.alert("Failed to cancel the shift!")})}}}})})});