$(document).ready(function(){$(".js-cancel-shift").click(function(n){var t=$(n.target);confirm("Are you sure you want to cancel this shift?")&&$.ajax({url:"/api/projects/"+t.attr("data-project-id")+"/shifts/delete/"+t.attr("data-shift-id"),method:"DELETE"}).done(function(){t.parents("tr").fadeOut(function(){$(this).remove()})}).fail(function(){alert("Failed to cancel the shift!")})})});