$(document).ready(function(){$(".js-cancel-shift").click(function(n){var t=$(n.target);bootbox.dialog({message:"Are you sure you want to cancel this shift?",title:"Confirm",buttons:{no:{label:"No",className:"btn-default",callback:function(){bootbox.hideAll()}},yes:{label:"Yes",className:"btn-danger",callback:function(){$.ajax({url:"/api/projects/"+t.attr("data-project-id")+"/shifts/delete/"+t.attr("data-shift-id"),method:"DELETE"}).done(function(){t.parents("tr").fadeOut(function(){$(this).remove()})}).fail(function(){bootbox.alert("Failed to cancel the shift!")})}}}})});$(".js-cancel-project").click(function(n){var t=$(n.target);bootbox.dialog({message:"Are you sure you want to cancel this Project? Every envolved person will get the cancel note then.",title:"Confirm Project Cancel",buttons:{no:{label:"No",className:"btn-default",callback:function(){bootbox.hideAll()}},yes:{label:"Yes",className:"btn-danger",callback:function(){$.ajax({url:"/projects/delete/"+t.attr("data-project-id"),method:"GET"}).done(function(){t.parents("tr").fadeOut(function(){$(this).remove()})}).fail(function(){bootbox.alert("Failed to cancel the Project!")})}}}})});$(".js-delete-worker").click(function(n){var t=$(n.target);bootbox.dialog({message:"Are you sure you want to delete this Worker? User will get the corresponding notice.",title:"Confirm Worker Delete",buttons:{no:{label:"No",className:"btn-default",callback:function(){bootbox.hideAll()}},yes:{label:"Yes",className:"btn-danger",callback:function(){$.ajax({url:"/workers/delete/"+t.attr("data-worker-id"),method:"GET"}).done(function(){t.parents("tr").fadeOut(function(){$(this).remove()})}).fail(function(){bootbox.alert("Failed to delete worker!")})}}}})})});